import React, { useCallback } from 'react';
import ReactDOM, { render } from 'react-dom';
import './index.css';

import tetris from './Tetris_T.png';
import githubicon from './github_icon.png';

var inDesc = false;

class Item extends React.Component{
  
  state = { xpos: 400 , ypos: 400 , drag: false ,
            pos1 : 0 , pos2 : 0,
            desc : "<h1>PLEASE ENTER DESCRIPTION</h1>",
            display_image : tetris };

  constructor(props){
    super(props);
  }

  // When the user clicks on the mouse
  elementDrag = (e) => {
    if(this.state.ypos < 200 && inDesc)
    {
      inDesc = false;
    }
    
    if(!inDesc)
    {
      e = e || window.event;
      e.preventDefault();

      this.setState(currentState => {
        return {
          xpos : currentState.pos1 - currentState.xrel,
          ypos : currentState.pos2 - currentState.yrel,
          pos1 : e.clientX,
          pos2 : e.clientY,
        };
      });
    }
  }

  // When the user lets go of the mouse
  closeDragElement = (e) => {
    if(this.state.ypos < 200)
    {
      // If to be displayed
      if(!inDesc) 
      {
        inDesc = true;
        
      }
    }
    else if(inDesc && this.state.ypos >= 200)
      inDesc = false;
    document.onmouseup = null;
    document.onmousemove = null;
  }

  // While the user is dragging the element
  dragMouseDown = (e) => {
    if(this.state.ypos < 200 && inDesc)
    {
      inDesc = false;
    }
    if(!inDesc)
    {
      e = e || window.event;
      e.preventDefault();

      this.setState(currentState => {
        return {
          xrel : e.clientX - currentState.xpos,
          yrel : e.clientY - currentState.ypos,
          pos1 : e.clientX,
          pos2 : e.clientY,
        };
      });

      document.onmouseup = this.closeDragElement;
      document.onmousemove = this.elementDrag;
    }
  }

  render(){

    let text;
    if(this.state.ypos < 200) {
      document.getElementById("description").innerHTML = this.state.desc;
      document.getElementById("content").style.boxShadow = "0 0 0 100vmax rgba(0,0,0,.3)";
    }
    

    return(
      <div id="item_description">
        <span id="description"></span>
        <img src={this.state.display_image}  
          alt="Tetris T Piece" 

          onMouseDown = {this.dragMouseDown}

          width="140" height="100"
          style={{
            position: "absolute",
            left: this.state.xpos + "px",
            top: this.state.ypos + "px",
          }}
        />
      </div>
    )
  }
}

class Tetris extends Item{
  constructor(props){
    super(props);
    this.state.desc = "<h1>Tetris Project - built with C++ and SFML</h1>";
  }
}

class GithubPiece extends Item{
  constructor(props){
    super(props);
    this.state.xpos = 700;
    this.state.ypos = 700;
    this.state.desc = "<h1>Github account</h1>";
    this.state.display_image = githubicon;
  }


}

class App extends React.Component{

    render() {
      return(
        <div className = 'container'>
          <div class="sidenav">
            <a href="#about">About</a>
            <a href="#services">Services</a>  
          </div>
          <div className = 'content'>
            <Tetris />
            <GithubPiece />
            <hr id="divide"></hr>
          </div>
          
        </div>
      )
    }
}

ReactDOM.render(
  
  <App />,
  document.getElementById('root')
);
