import React, { useCallback } from 'react';
import ReactDOM, { render } from 'react-dom';
import './index.css';

import mainLogo from './Tetris_T.png';

class Item extends React.Component{
  state = { xpos: 0 , ypos: 0 , drag: true };

  move = (e) => {
    var newX = e.clientX - 10;
    var newY = e.clientY - 10;

    if(this.state.drag)
    this.setState(currentState => {
      return {xpos: e.clientX - 10, ypos: e.clientY - 10};
    });

  };

  click = (e) => {
    var newX = e.clientX - 10;
    var newY = e.clientY - 10;

    this.setState(currentState => {
      return {drag : !this.state.drag};
    });

  };

  constructor(props){
    super(props);
    const FREE = 0;
    const DRAG = 1;

    //alert(mainLogo)

    document.addEventListener("mousemove", this.move, false);
    //is.addEventListener("click", , false);
  }

  render(){
    return(
      <div>
        <img src={mainLogo}  
          alt="Tetris T Piece" 
          
          onClick="this.click()"

          width="140" height="100"
          style={{
            position: "absolute",
            left: this.state.xpos + "px",
            top: this.state.ypos + "px",
          }}
          
          /*
          onClick={
            () => {
              this.state=!this.state
              alert(this.state)
            }
          }*/

          
        />

      </div>
    )
  }
}

class App extends React.Component{
    render() {
      return(
        <div>
          <Item />
        </div>
      )
    }
}

ReactDOM.render(
  <App />,
  document.getElementById('root')
);
